import { Component, HostListener, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from 'src/app/services/data.service';

import { CdkDragDrop, CdkDragEnd, moveItemInArray } from '@angular/cdk/drag-drop';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.scss'],
})
export class BoardComponent implements OnInit {
  lists: any[] = [];
  boardId: string | null = null;
  editTitle: any = {};
  editCard: any = {};
  boardInfo: any = null;
  titleChanged = false;

  listCards: any = {};
  addUserEmail = '';

  constructor(
    private route: ActivatedRoute,
    private dataService: DataService,
    private router: Router
  ) {}

  async ngOnInit() {
    this.boardId = this.route.snapshot.paramMap.get('id');
    if (this.boardId) {
      this.boardInfo = (await this.dataService.getBoardInfo(this.boardId)).data;

      this.lists = await this.dataService.getBoardLists(this.boardId);

      // Retrieve cards for each list
      for (let list of this.lists) {
        this.listCards[list.id] = await this.dataService.getListCards(list.id);
      }

      this.handleRealtimeUpdates();
    }
  }

  //
  // BOARD logic
  //
  async saveBoardTitle() {
    await this.dataService.updateBoard(this.boardInfo);
    this.titleChanged = false;
    window.location.reload();
  }

  async deleteBoard() {
    await this.dataService.deleteBoard(this.boardInfo);
    this.router.navigateByUrl('/home/workspace');
  }

  //
  // LISTS logic
  //
  async addList() {
    const newList = await this.dataService.addBoardList(
      this.boardId!,
      this.lists.length
    );
  }

  editingTitle(list: any, edit = false) {
    this.editTitle[list.id] = edit;
  }

  async updateListTitle(list: any) {
    await this.dataService.updateBoardList(list);
    this.editingTitle(list, false);
  }

  async deleteBoardList(list: any) {
    await this.dataService.deleteBoardList(list);
  }

  
  onCardDropped(event: CdkDragDrop<any[]>, targetListId: string) {
    if (event.previousContainer === event.container) {
      // Reordenar dentro de la misma lista
      moveItemInArray(
        event.container.data,
        event.previousIndex,
        event.currentIndex
      );
    } else {
      // Mover la tarjeta a la lista de destino
      const previousListId = event.previousContainer.id;
      const card = event.item.data;
      const currentIndex = event.currentIndex;
  
      // Eliminar la tarjeta de la lista anterior
      this.listCards[previousListId] = this.listCards[previousListId].filter((c: { id: any; }) => c.id !== card.id);
  
      // Insertar la tarjeta en la lista de destino en la posición actual
      this.listCards[targetListId].splice(currentIndex, 0, card);
    }
  }
  
  onCardDragEnded(event: any, listId: string) {
    if (!event.item.dropContainer) {
      // Si la tarjeta no se soltó en un contenedor, devolverla a su posición original
      moveItemInArray(
        this.listCards[listId],
        event.currentIndex,
        event.previousIndex
      );
    }
  }


  //
  // CARDS logic
  //
  newCard(list: any){

  }
  async addCard(list: any) {
    await this.dataService.addListCard(
      list.id,
      this.boardId!,
      this.listCards[list.id].length,
    );
    
  }

  editingCard(card: any, edit = false) {
    this.editCard[card.id] = edit;
  }

  async updateCard(card: any) {
    await this.dataService.updateCard(card);
    this.editingCard(card, false);
  }

  async deleteCard(card: any) {
    await this.dataService.deleteCard(card);
  }

  // Invites
  async addUser() {
    const res = await this.dataService.addUserToBoard(
      this.boardId!,
      this.addUserEmail
    );
    this.addUserEmail = '';
  }

  @HostListener('document:keydown', ['$event']) onKeydownHandler(
    event: KeyboardEvent
  ) {
    if (event.keyCode === 27) {
      // Close whatever needs to be closed!
      this.titleChanged = false;

      Object.keys(this.editCard).map((item) => {
        this.editCard[item] = false;
        return item;
      });

      Object.keys(this.editTitle).map((item) => {
        this.editTitle[item] = false;
        return item;
      });
    }
  }

  handleRealtimeUpdates() {
    this.dataService.getTableChanges().subscribe((update: any) => {
      const record = update.new?.id ? update.new : update.old;
      const event = update.eventType;

      if (!record) return;

      if (update.table == 'cards') {
        if (event === 'INSERT') {
          this.listCards[record.list_id].push(record);
        } else if (event === 'UPDATE') {
          const newArr = [];

          for (let card of this.listCards[record.list_id]) {
            if (card.id == record.id) {
              card = record;
            }
            newArr.push(card);
          }
          this.listCards[record.list_id] = newArr;
        } else if (event === 'DELETE') {
          this.listCards[record.list_id] = this.listCards[
            record.list_id
          ].filter((card: any) => card.id !== record.id);
        }
      } else if (update.table == 'lists') {
        if (event === 'INSERT') {
          this.lists.push(record);
          this.listCards[record.id] = [];
        } else if (event === 'UPDATE') {
          this.lists.filter((list: any) => list.id === record.id)[0] = record;

          const newArr = [];

          for (let list of this.lists) {
            if (list.id == record.id) {
              list = record;
            }
            newArr.push(list);
          }
          this.lists = newArr;
        } else if (event === 'DELETE') {
          this.lists = this.lists.filter((list: any) => list.id !== record.id);
        }
      }
    });
  }
}
<header class="bg-emerald-600">
  <nav class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
    <div
      class="w-full py-6 flex items-center justify-between border-b border-emerald-500 lg:border-none"
    >
      <div class="flex items-center">
        <a routerLink="/home/workspace" class="font-semibold" style="color: white;">
           Atras
        </a>
      </div>
      <div class="flex gap-4">
        <input
          *ngIf="boardInfo"
          (ngModelChange)="titleChanged = true"
          class="ml-10 space-x-4 text-white font-bold bg-rgb-32-62-129"
          [(ngModel)]="boardInfo.title"
        />
        <button
          class="font-medium"
          *ngIf="titleChanged"
          (click)="saveBoardTitle()"
        >
          Guardar
        </button>
      </div>

      <div class="flex">
        <button
          class="font-medium text-small text-red-700"
          (click)="deleteBoard()"
        >
          Eliminar Tarjeta
        </button>
      </div>
    </div>
  </nav>
</header>

<main class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
  <div
    class="grid grid-cols-2 gap-x-4 gap-y-8 sm:grid-cols-3 sm:gap-x-6 lg:grid-cols-4 xl:gap-x-8"
  >
    <!-- ITERATE ALL LISTS -->
    <div
      *ngFor="let list of lists"
      class="relative min-h-52 h-auto rounded font-semibold text-sm py-4 px-4"
      style="background-color: rgb(32, 62, 129);"
      cdkDropList
      [cdkDropListData]="listCards[list.id]"
      (cdkDropListDropped)="onCardDropped($event, list.id)"
    >
      <div class="flex gap-2 pb-4">
        <p
          (click)="editingTitle(list, true)"
          class="hover:cursor-pointer text-white"
          *ngIf="!editTitle[list.id]"
        >
          {{ list.title }}
        </p>
        <input
          [(ngModel)]="list.title"
          *ngIf="editTitle[list.id]"
          class="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-emerald-500 focus:border-emerald-500 sm:text-sm"
        />
        <button
          class="font-medium"
          *ngIf="editTitle[list.id]"
          (click)="updateListTitle(list)"
        >
          Guardar
        </button>
      </div>

      <!-- ITERATE LIST CARDS -->
      <div class="flex flex-col items-center gap-2">
        <div
          class="h-auto w-full flex flex-col gap-2 hover:cursor-pointer"
          *ngFor="let card of listCards[list.id]"
          cdkDrag
          [cdkDragData]="card"
          (click)="editingCard(card, true)"
        >
          <p class="h-15 py-2 px-2 bg-white inline-block" style="overflow:hidden; display: flex;" *ngIf="!editCard[card.id]">
            {{ card.title }}
          </p>
          <input
            [(ngModel)]="card.title"
            *ngIf="editCard[card.id]"
            class="block px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-emerald-500 focus:border-emerald-500 sm:text-sm"
          />
          <div class="flex align-items-center justify-between">
            <button
              class="font-medium"
              *ngIf="editCard[card.id]"
              (click)="updateCard(card)"
            >
              Actualizar
            </button>

            <button
              class="font-medium text-red-600"
              *ngIf="editCard[card.id]"
              (click)="deleteCard(card)"
            >
              Borrar
            </button>
          </div>
        </div>
        <div
          (click)="addCard(list)"
          class="pt-8 text-gray-500 hover:cursor-pointer">
          + Agregar Tarjeta
        </div>
        <button
        class="font-medium text-small text-red-700 hover:text-white" 
          (click)="deleteBoardList(list)"
        >
          Eliminar Lista
        </button>
      </div>
    </div>

    <div
      (click)="addList()"
      class="relative min-h-auto h-16 rounded font-semibold text-lg py-4 px-4"
  style="background-color: rgb(32, 62, 129); color: white; "
    >
      + Nueva Lista
    </div>
  </div>

  <div class="flex gap-4 py-12 items-center">
    <span class="text-3xl font-extrabold text-gray-900 block">Invitar</span>

    <input
      [(ngModel)]="addUserEmail"
      placeholder="ejemplo@xd.com"
      class="block px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-emerald-500 focus:border-emerald-500 sm:text-sm"
    />
    <button
      (click)="addUser()"
      class="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded shadow-sm text-white bg-emerald-600 hover:bg-emerald-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-emerald-500"
    >
      Invitar
    </button>
  </div>
</main>
